import React, { Component } from "react";
import Subcounter from "./subcounter";

class Counters extends Component {
  state = {
    counters: [
      { id: 1, value: 4 },
      { id: 2, value: 0 },
      { id: 3, value: 0 },
      { id: 4, value: 0 }
    ]
  };

  handleDelete = () => {
    console.log("Event Handler Called");
  };
  render() {
    return (
      <div>
        {/* <Subcounter />
        <Subcounter />
        <Subcounter />
        <Subcounter /> */}

        {this.state.counters.map(counter => (
          //<Subcounter key={counter.id} value={counter.value} selected={true} />
          //   此处传给subcounter这些子components 以value和selected
          <Subcounter key={counter.id} value={counter.value} id={counter.id}>
            {/* <h4>Title</h4> */}
            <h4>Counter #{counter.id}</h4>
            {/* 这部分在h4包起来的就是被当作contained components（这里是subcounter）的children */}
          </Subcounter>
        ))}
      </div>
    );
  }
}

export default Counters;
